import React from 'react';
import stl from './Users.module.css';
import * as axios from 'axios';

const Users = (props) => {

    if (props.users.length === 0) {

        axios.get("https://social-network.samuraijs.com/api/1.0/users").then(response => {
            props.setUsers(response.data.items);
        });

        /* props.setUsers(
            [
                {id: 1,
                    photoUrl: 'http://aniavatars.com/data/avatars/90/243.png',
                    followed: true, fullName: 'Vasya', status: 'Straus find my mouse', location: {city: 'Alamutu', country: 'Balamutu'}},
                {id: 2,
                    photoUrl: 'http://aniavatars.com/data/avatars/90/243.png',
                    followed: false, fullName: 'Petya', status: 'Unibabatua mumuki', location: {city: 'Mbanini', country: 'Putalkunsti'}},
                {id: 3,
                    photoUrl: 'http://aniavatars.com/data/avatars/90/243.png',
                    followed: true, fullName: 'BOris', status: 'Pelemelemeni tumbas', location: {city: 'Ebshte', country: 'Bdrlyupkutl'}}
            ]
        ) */
    }


    return (
        <div>
            {
                props.users.map( u => <div key={u.id}>
                    <span>
                        <div>
                            <img src={u.photoUrl} className={stl.userPhoto} alt="ava"/>
                        </div>
                        <div>
                            { u.followed
                                ? <button onClick={ () => { props.unfollow(u.id) }}>unfollow</button>
                                : <button onClick={ () => { props.follow(u.id) }}>follow</button>}
                        </div>
                    </span>
                    <span>
                        <span>
                            <div>{u.name}</div>
                            <div>{u.status}</div>
                        </span>
                        <span>
                            <div>{u.location.country}</div>
                            <div>{u.location.city}</div>
                        </span>
                    </span>
                    </div>)
            }
        </div>
    )
};

export default Users;
