import React from 'react';
import {addPostActionCreator, updateNewPostTextActionCreator} from '../../../redux/profilesReducer';
import Posts from './Posts';

const PostsContainer = (props) => {

    let state = props.store.getState();

    let addPost = () => {
        props.store.dispatch(addPostActionCreator());
    };

    let onPostChange = (text) => {
        let action = updateNewPostTextActionCreator(text);
        props.storeore.dispatch(action);
    };

    return (
        <Posts updateNewPostText={onPostChange} addPost={addPost} postsData={state.profilesPage.postsData} />
    );
}

export default PostsContainer;
