import React from "react";
import stl from "./Dialogs.module.css";
import { NavLink } from "react-router-dom";

const DialogsItem = (props) => {

  let path = '/dialogs/' + props.id;

  return (
    <div className={stl.dialogs__item}>
      <NavLink to={path}>{props.name}</NavLink>
    </div>
  );
}

const Message = (props) => {
  return (
    <div className={stl.message}>{props.message}</div>
  );
}

const Dialogs = (props) => {

  let dialogsData = [
    {id: 1, name: 'Тимофей'},
    {id: 2, name: 'Ирина'},
    {id: 3, name: 'Таисия'},
    {id: 4, name: 'Эммануил'},
    {id: 5, name: 'Татьяна'},
    {id: 6, name: 'Екатерина'},
    {id: 7, name: 'Михаил'}
  ];

  let dialogsElements = dialogsData.map(
    dialog => <DialogsItem name={dialog.name} id={dialog.id} />
  );

  let messagesData = [
    {id: 1, message: 'Hello!'},
    {id: 2, message: 'Пащёль нафик, с новым годом!'},
    {id: 3, message: 'Свежая шутка, ха-ха :-|'},
    {id: 4, message: 'Хе-хе!'},
    {id: 5, message: 'Хи-хи!'}
  ];

  return (
    <div className={stl.dialogs}>
      <div className={stl.dialogs__items}>
        <DialogsItem name={dialogsData[0].name} id={dialogsData[0].id} />
        <DialogsItem name={dialogsData[1].name} id={dialogsData[1].id} />
      </div>
      <div className={stl.messages}>
        <Message message={messagesData[0].message} />
        <Message message={messagesData[1].message} />
      </div>
    </div>
  );
};

export default Dialogs;

//**************************************************//
// =======   К О Н С П Е К Т   =====================//
//**************************************************//

