import profilesReducer from "./profilesReducer";

const ADD_POST = 'ADD-POST';
const UPDATE_NEW_POST_TEXT = 'UPDATE-NEW-POST-TEXT';

const SEND_MESSAGE = 'SEND-MESSAGE';
const UPDATE_NEW_MESSAGE_BODY = 'UPDATE-NEW-MESSAGE-BODY';

let store = {
  _state: {
    profilesPage: {
      postsData: [
        {
          id: 1,
          message: "А чё, думали я по-русски писать разучился?",
          likes: 12
        },
        { id: 2, message: "Second post - а енто типа по енглишу :-)", likes: 15 }
      ],
      newPostText: ''
    },
    dialogsPage: {
      dialogs: [
        { id: 1, name: "Тимофей" },
        { id: 2, name: "Ирина" },
        { id: 3, name: "Таисия" },
        { id: 4, name: "Эммануил" },
        { id: 5, name: "Татьяна" },
        { id: 6, name: "Екатерина" },
        { id: 7, name: "Михаил" }
      ],
      messages: [
        { id: 1, message: "Hello!" },
        { id: 2, message: "Пащёль нафик, с новым годом!" },
        { id: 3, message: "Свежая шутка, ха-ха :-|" },
        { id: 4, message: "Хе-хе!" },
        { id: 5, message: "Хи-хи!" }
      ],
      newMessage: 's'
    },
    sidebar: {}
  },
  _callSubscriber() {
    console.log('State changed');
  },
  getState(){
    return this._state;
  },
  subscribe(observer) {
    this._callSubscriber = observer;
  },

  dispatch(action) {
    this._state.profilesPage = profilesReducer(this._state.profilesPage, action);
    /* if(action.type === ADD_POST) {
      let newPost = {
        id: 3,
        message: this._state.profilesPage.newPostText,
        likes: 2
      };

      this._state.profilesPage.postsData.push(newPost);
      this._state.profilesPage.newPostText = '';
      this._callSubscriber(this._state);
    }
    else if(action.type === UPDATE_NEW_POST_TEXT) {
      this._state.profilesPage.newPostText = action.newText;
      this._callSubscriber(this._state);
    }
    else */ if(action.type === SEND_MESSAGE) {
      let newMessage = {
        id: 6,
        message: this._state.dialogsPage.newMessage
      };

      this._state.dialogsPage.messages.push(newMessage);
      this._state.dialogsPage.newMessage = '';
      this._callSubscriber(this._state);
    }
    else if(action.type === UPDATE_NEW_MESSAGE_BODY) {
      this._state.dialogsPage.newMessage = action.body;
      this._callSubscriber(this._state);
    }
  }
};


export const addPostActionCreator = () => ({type: ADD_POST});
export const updateNewPostTextActionCreator = (text) =>
                ({type: UPDATE_NEW_POST_TEXT, newText: text});

export const sendMessageCreator = () => ({type: SEND_MESSAGE});
export const updateNewMessageTextCreator = (body) =>
                ({type: UPDATE_NEW_MESSAGE_BODY, body: body});

export default store;

window.store = store;
