import React from 'react';
import stl from './Posts.module.css';
import Post from './Post/Post';

const Posts = (props) => {

    /* let postsData = [
        {id: 1, message: 'А чё, думали я по-русски писать разучился?', likes: 12},
        {id: 2, message: 'Second post - а енто типа по енглишу :-)', likes: 15}
    ]; */

    let postsData = props.postsData;

    let postsElements = [];

    /* let postsElements = postsData.map (
        p => <Post likes={p.likes} message={p.message} />
    ); */

    return (
        <div className={stl.postsBlock}>
            <h3>My posts</h3>
            <div>
                <div>
                    <textarea></textarea>
                </div>
                <div>
                    <button>Add Post</button>
                </div>
            </div>
            <div className={stl.posts}>
                { postsElements }
            </div>
        </div>
    );
}

export default Posts;

