import React from 'react';
import { BrowserRouter,Route } from 'react-router-dom';
import './App.css';
import Header from './components/Header/Header';
import Navbar from './components/Navbar/Navbar';
import Profile from './components/Profile/Profile';
import Dialogs from './components/Dialogs/Dialogs';
import News from './components/News/News';
import Music from './components/Music/Music';
import Settings from './components/Settings/Settings';

const App = (props) => {

  let dialogs = props.state.dialogsPage.dialogs;
  let messages = props.state.dialogsPage.messages;
  let postsData = props.state.profilesPage.postsData;

  return (
    <BrowserRouter>
      <div className="app-wrapper">
        <Header />
        <Navbar />
        <div className="app-wrapper__content">
          <Route path='/profile' render={ () => <Profile postsData={postsData} /> } />
          <Route path='/dialogs' render={ () => <Dialogs dialogs={dialogs} messages={messages} /> } />
          <Route path='/news' component={ News } />
          <Route path='/music' component={ Music } />
          <Route path='/settings' component={Settings} />
        </div>
      </div>
    </BrowserRouter>
  );
}

export default App;

//**************************************************//
// =======   К О Н С П Е К Т   =====================//
//**************************************************//

// <BrowserRouter>  -  обязательно оборачивать <Route>
// <Route path='' component={} />  -  отображать компонент из {},
                      //  если в адресной строке ссылка из path=''
            //  exact  -  только если URL точно совпадает
