let store = {
  _state: {
    profilesPage: {
      postsData: [
        {
          id: 1,
          message: "А чё, думали я по-русски писать разучился?",
          likes: 12
        },
        { id: 2, message: "Second post - а енто типа по енглишу :-)", likes: 15 }
      ],
      newPostText: 'M1982'
    },
    dialogsPage: {
      dialogs: [
        { id: 1, name: "Тимофей" },
        { id: 2, name: "Ирина" },
        { id: 3, name: "Таисия" },
        { id: 4, name: "Эммануил" },
        { id: 5, name: "Татьяна" },
        { id: 6, name: "Екатерина" },
        { id: 7, name: "Михаил" }
      ],
      messages: [
        { id: 1, message: "Hello!" },
        { id: 2, message: "Пащёль нафик, с новым годом!" },
        { id: 3, message: "Свежая шутка, ха-ха :-|" },
        { id: 4, message: "Хе-хе!" },
        { id: 5, message: "Хи-хи!" }
      ]
    },
    sidebar: {}
  },
  getState(){
    return this._state;
  },
  _callSubscriber() {
    console.log('State changed');
  },
  addPost() {
    let newPost = {
      id: 3,
      message: this._state.profilesPage.newPostText,
      likes: 2
    };

    this._state.profilesPage.postsData.push(newPost);
    this._state.profilesPage.newPostText = '';
    this._callSubscriber(this._state);

console.log(this._state.profilesPage);

  },
  updateNewPostText(newText) {
    this._state.profilesPage.newPostText = newText;
    this._callSubscriber(this._state);

    console.log(this._state.profilesPage);

  },
  subscribe(observer) {
    this._callSubscriber = observer;
  }
};


export default store;

window.store = store;
