import React from 'react';
import stl from './Posts.module.css';
import Post from './Post/Post';

const Posts = () => {

    let postsData = [
        {id: 1, message: 'А чё, думали я по-русски писать разучился?', likes: 12},
        {id: 2, message: 'Second post - а енто типа по енглишу :-)', likes: 15}
    ];

    return (
        <div className={stl.postsBlock}>
            <h3>My posts</h3>
            <div>
                <div>
                    <textarea></textarea>
                </div>
                <div>
                    <button>Add Post</button>
                </div>
            </div>
            <div className={stl.posts}>
                <Post likes={postsData[0].likes} message={postsData[0].message} />
                <Post likes={postsData[1].likes} message={postsData[1].message} />
            </div>
        </div>
    );
}

export default Posts;

//**************************************************//
// =======   К О Н С П Е К Т   =====================//
//**************************************************//

