import React from "react";
import stl from "./Dialogs.module.css";
import DialogsItem from './DialogsItem/DialogsItem';


const Message = (props) => {
  return (
    <div className={stl.message}>{props.message}</div>
  );
}

const Dialogs = (props) => {

  let dialogs = [
    {id: 1, name: 'Тимофей'},
    {id: 2, name: 'Ирина'},
    {id: 3, name: 'Таисия'},
    {id: 4, name: 'Эммануил'},
    {id: 5, name: 'Татьяна'},
    {id: 6, name: 'Екатерина'},
    {id: 7, name: 'Михаил'}
  ];

  let messages = [
    {id: 1, message: 'Hello!'},
    {id: 2, message: 'Пащёль нафик, с новым годом!'},
    {id: 3, message: 'Свежая шутка, ха-ха :-|'},
    {id: 4, message: 'Хе-хе!'},
    {id: 5, message: 'Хи-хи!'}
  ];

  let dialogsElements = dialogs.map (
    d => <DialogsItem name={d.name} id={d.id} />
  );

  let messagesElements = messages.map (
    m => <Message message={m.message} />
  );

  return (
    <div className={stl.dialogs}>
      <div className={stl.dialogs__items}>
        { dialogsElements }
      </div>
      <div className={stl.messages}>
        { messagesElements }
      </div>
    </div>
  );
};

export default Dialogs;

//**************************************************//
// =======   К О Н С П Е К Т   =====================//
//**************************************************//

