import React from "react";
import stl from "./Dialogs.module.css";
import { NavLink } from "react-router-dom";

const DialogsItem = (props) => {

  let path = '/dialogs/' + props.id;
  console.log(path);

  return (
    <div className={stl.dialogs__item}>
      <NavLink to={path}>{props.name}</NavLink>
    </div>
  );
}

const Dialogs = () => {
  return (
    <div className={stl.dialogs}>
      <div className={stl.dialogs__items}>
        <DialogsItem name='Тимофей' id='1' />

        <div className={stl.dialogs__item}>
          <NavLink to="/messages/2">Ирина</NavLink>
        </div>
        <div className={`${stl.dialogs__item} ${stl.active}`}>
          <NavLink to="/messages/3">Таисия</NavLink>
        </div>
        <div className={stl.dialogs__item}>
          <NavLink to="/messages/4">Эммануил</NavLink>
        </div>
        <div className={stl.dialogs__item}>
          <NavLink to="/messages/5">Татьяна</NavLink>
        </div>
        <div className={stl.dialogs__item}>
          <NavLink to="/messages/6">Екатерина</NavLink>
        </div>
        <div className={stl.dialogs__item}>
          <NavLink to="/messages/7">Михаил</NavLink>
        </div>
      </div>
      <div className={stl.messages}>
        <div className={stl.message}>Hello!</div>
        <div className={stl.message}>Пащёль нафик, с новым годом!</div>
        <div className={stl.message}>Свежая шутка, ха-ха :-|</div>
      </div>
    </div>
  );
};

export default Dialogs;

//**************************************************//
// =======   К О Н С П Е К Т   =====================//
//**************************************************//

